mapper.update:
UPDATE uaa_user
username = #{username}, password = #{password}, email = #{email}, mobile = #{mobile}, creator = #{creator}, create_time = #{create_time}, user_enable = #{user_enable}, user_status = #{user_status}, last_login_time = #{last_login_time}, last_login_addr = #{last_login_addr},
WHERE user_id = #{user_id}

mapper.select:
SELECT * FROM uaa_user

	AND username LIKE '%${username}%'
	AND user_enable = #{user_enable}
	AND user_status = #{user_status}

ORDER BY username ASC
